// Card.js

import React from 'react';
import { Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import data from "../Cards/CardData.json";
import "../Cards/Card.css";

export default Card;
 function Card({ searchTerm }) {
    return (
        <div className='section'>
            <div className='cocktails-center'>
                {data.filter((val) => {
                    if (searchTerm === "") {
                        return true;
                    } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())) {
                        return true;
                    }
                    return false;
                }).map((val) => (
                    <Col key={val.id} className='cocktail' lg={4}>
                        <div className='ww'>
                            <img src={val.img} alt={val.name} />
                        </div>
                        <div className='ss'>
                            <h3 className='fs-2'>{val.name}</h3>
                            <div className='tt'>
                                <div>
                                    <p>{val.glass}</p>
                                    <p className='pp'>{val.info}</p>
                                </div>
                                <Link to={`/detail/${val.id}`} className="button-40">Details</Link>
                            </div>
                        </div>
                    </Col>
                ))}
            </div>
        </div>
    )
}
