import React, { useState } from 'react';
import { Col } from 'react-bootstrap';
import { Modal } from '../Modal/Modal'; // Assuming Modal component is in the same directory
import data from "../Cards/CardData.json";
import "../Cards/Card.css";

export function Card({ searchTerm }) {
    const [selectedItemId, setSelectedItemId] = useState(null);
    const [modalOpen, setModalOpen] = useState(false);

    function showDetail(id) {
        setSelectedItemId(id);
        setModalOpen(true);
    }

    function hideModal() {
        setModalOpen(false);
    }

    return (
        <div className='section'>
            <div className='cocktails-center'>
                {data.filter((val) => {
                    if (searchTerm === "") {
                        return true;
                    } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())) {
                        return true;
                    }
                    return false;
                }).map((val) => (
                    <Col key={val.id} className='cocktail' lg={4}>
                        <div className='ww'>
                            <img src={val.img} alt={val.name} />
                        </div>
                        <div className='ss'>
                            <h3 className='fs-2'>{val.name}</h3>
                            <div className='tt'>
                                <div>
                                    <p>{val.glass}</p>
                                    <p className='pp'>{val.info}</p>
                                </div>
                                <button onClick={() => showDetail(val.id)} className="button-40">Details</button>
                            </div>
                        </div>
                    </Col>
                ))}
            </div>
            {/* Modal */}
            {modalOpen && (
                <Modal onClose={hideModal}>
                    {/* Render modal content here */}
                    {/* You can use data[selectedItemId] to access the detail of the selected item */}
                </Modal>
            )}
        </div>
    )
}
