import React from 'react';
import { useParams } from 'react-router-dom';
import data from "../Cards/CardData.json";
import { Navbar } from "../Navbar/Navbar"
import "../Detail/Detail.css";

export function Detail() {
    const { id } = useParams();

    const selectedCard = data.find(card => card.id === parseInt(id));

    if (!selectedCard) {
        return <div>Card not found</div>;
    }

    return (
        <div>
            <Navbar />

            <div className='container'>
                <div className='row'>
                    <div className='cocktail' style={{flexDirection:"row",flexWrap:"wrap"}}>
                        <div className='box1' style={{ width: "50%" }}>
                            <img className='drink-img' src={selectedCard.img} alt={selectedCard.name}  style={{borderRadius:"17px",margin:"0"}}/>
                        </div>

                        <div className='box2' style={{ width: "50%" }}>
                            <h2 >{selectedCard.name}</h2>
                            <h2 >{selectedCard.category}</h2>
                            <p className='drink-info'>{selectedCard.info}</p>
                            <p className='.section-title'>Glass: {selectedCard.glass}</p>
                            <p className='drink-info'>{selectedCard.instruction}</p>
                            <p className='drink-info'>{selectedCard.ingredients}</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
