import React from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to get the id from the URL
import data from "../Cards/CardData.json";
import {Card} from "../Cards/Card"

export function DetailView() {
    const { id } = useParams(); // Get the id parameter from the URL

    // Find the selected card based on the id
    const selectedCard = data.find(Card => Card.id === parseInt(id));

    if (!selectedCard) {
        return <div>Card not found</div>; // Handle case when card is not found
    }

    return (
        <div>
            <h2>{selectedCard.name}</h2>
            <img src={selectedCard.img} alt={selectedCard.name} />
            <p>Glass: {selectedCard.glass}</p>
            <p>{selectedCard.info}</p>
            {/* Render other details of the selected card as needed */}
        </div>
    );
}
