import React from 'react';
import { useParams } from 'react-router-dom';
import data from "../Cards/CardData.json";
import {Navbar} from "../Navbar/Navbar"

export function Detail() {
    const { id } = useParams();

    const selectedCard = data.find(card => card.id === parseInt(id));

    if (!selectedCard) {
        return <div>Card not found</div>;
    }

    return (
        <div>
            <Navbar />

            <div>
                <h2 className='cocktail'>{selectedCard.name}</h2>
                <img className='drink img' src={selectedCard.img} alt={selectedCard.name} />
                <p>Glass: {selectedCard.glass}</p>
                <p>{selectedCard.info}</p>
            </div>
        </div>
    );
}
