import React from 'react';
import { Search } from "../Search/Search"
import "../Cards/Card.css";
import data from "../Cards/CardData.json"
import { Col } from 'react-bootstrap';


export function Card({ searchTerm }) {
  
    function detail(item){
        {item.map((d)=>(
            <div>
                {d.img}
            </div>
        ))}
    }

    return (

        <div className='section'>
            <div className='cocktails-center'>
                {data.filter((val) => {
                    if (searchTerm === "") {
                        return val;
                    } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())) {
                        return val;
                    }
                }).map((val) => (
                    <Col key={val.id} className='cocktail' lg={4}>
                        <div className='ww'>
                            <img src={val.img} alt={val.name} />
                        </div>
                        <div className='ss'>

                            <h3 className='fs-2'>{val.name}</h3>
                            <div className='tt'>
                                <div>
                                    <p>{val.glass}</p>
                                    <p className='pp'>{val.info}</p>
                                </div>
                                <button onClick={detail(val.id)} className="button-40"  role="button">Details</button>
                            </div>
                        </div>
                    </Col>
                ))}
            </div>

        </div>
    )
}

// export default Card
